import { useContext, useState } from 'react'
import { GlobalStoreContext } from '../store'
import Box from '@mui/material/Box';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import ListItem from '@mui/material/ListItem';
import TextField from '@mui/material/TextField';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import ThumbDownAltIcon from '@mui/icons-material/ThumbDownAlt';
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';
import KeyboardDoubleArrowUpIcon from '@mui/icons-material/KeyboardDoubleArrowUp';
import List from '@mui/material/List';
import SongCard from './SongCard.js'
import apis from '../store/store-request-api';
import WorkspaceScreen from './WorkspaceScreen';
import AddIcon from '@mui/icons-material/Add';
import MUIEditSongModal from './MUIEditSongModal'
import MUIRemoveSongModal from './MUIRemoveSongModal'

/*
    This is a card in our list of top 5 lists. It lets select
    a list for editing and it has controls for changing its 
    name or deleting it.

    @author McKilla Gorilla
*/
function ListCard(props) {
    const { store } = useContext(GlobalStoreContext);
    const [editActive, setEditActive] = useState(false);
    const [text, setText] = useState("");
    const { idNamePair, selected } = props;

    function handlelistClick(event) {
        if(event.detail === 2){
            event.stopPropagation();
            toggleEdit();
        }
    }

    function handleAddNewSong(event) {
        event.stopPropagation();
        console.log("ADDIDNGGG SOOONNNGGG")
        store.addNewSong();
    }

    function toggleEdit() {
        let newActive = !editActive;
        if (newActive) {
            store.setIsListNameEditActive();
        }
        setEditActive(newActive);
    }

    async function handleDeleteList(event, id) {
        event.stopPropagation();
        let _id = event.target.id;
        _id = ("" + _id).substring("delete-list-".length);
        store.markListForDeletion(id);
    }

    function handleKeyPress(event) {
        if (event.code === "Enter") {
            let id = event.target.id.substring("list-".length);
            store.changeListName(id, text);
            toggleEdit();
        }
    }
    function handleUpdateText(event) {
        setText(event.target.value);
    }

    async function handleExpand(event, id) {
        event.stopPropagation();
        store.setCurrentList(id);
    }

    async function handlePublish(event, id){
        event.stopPropagation();
        store.publishPlaylist(id);
    }

    async function handleMinimize(event, id) {
        event.stopPropagation();
        store.closeCurrentList();
    }

    let selectClass = "unselected-list-card";
    if (selected) {
        selectClass = "selected-list-card";
    }
    let cardStatus = false;
    if (store.isListNameEditActive) {
        cardStatus = true;
    }

    let modalJSX = "";
    if (store.isEditSongModalOpen()) {
        modalJSX = <MUIEditSongModal />;
    }
    else if (store.isRemoveSongModalOpen()) {
        modalJSX = <MUIRemoveSongModal />;
    }

    let month = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
    let datePublished = "no";
    let likeDislike = null;
    let publishListen = null;
    if(idNamePair.playlist.published !== "no"){
        datePublished = month[idNamePair.playlist.published.substring(5, 7)-1] + " " + idNamePair.playlist.published.substring(8, 10) + ", " + idNamePair.playlist.published.substring(0, 4);
        likeDislike=
            <Grid item md={4}>
                <IconButton aria-label='like'>
                    <ThumbUpOffAltIcon style={{fontSize:'30pt'}} />
                </IconButton>
                <Typography display="inline">{idNamePair.playlist.likes}</Typography>
                <IconButton aria-label='dislike'>
                    <ThumbDownOffAltIcon style={{fontSize:'30pt'}} />
                </IconButton>
                <Typography display="inline">{idNamePair.playlist.dislikes}</Typography>
            </Grid>;
        publishListen = 
            <Grid item md={11}>
                <Typography display="inline" sx={{fontSize: 14}}>{"Published: "}</Typography>
                <Typography display="inline" sx={{color: "red", fontSize: 14}}>{datePublished}</Typography>
                <Typography display="inline" sx={{fontSize: 14}}>{"Listens: "}</Typography>
                <Typography display="inline" sx={{color: "green", fontSize: 14}}>{"LISTENS"}</Typography>
            </Grid>

    }


    //Normal Card #########################################################################################################################################################
    let cardElement =
        <ListItem
            id={idNamePair._id}
            key={idNamePair._id}
            sx={{border:2, borderRadius:"15px", p: 1, bgcolor: '#8000F0', marginBottom:"10px", display: 'flex', height: 140}}
            button
            onClick={(event) => {
                handlelistClick(event)
            }}
        > 
        <Container>
        <Grid container>
            <Grid item md={8}>
                <Typography sx={{fontSize: 50}}>{idNamePair.name}</Typography>
            </Grid>
            {likeDislike}
            <Grid item md={12}> 
                <Typography sx={{fontSize: 20}}>{"By: " + idNamePair.playlist.userName}</Typography>
            </Grid>
            {publishListen}
            <Grid item md={1}>
                <IconButton sx={{}} onClick={(event) => {handleExpand(event, idNamePair._id)}} aria-label='delete'>
                    <KeyboardDoubleArrowDownIcon sx={{fontSize:'25pt'}} />
                </IconButton>
            </Grid>
            </Grid>
            </Container>
        </ListItem>
    //Expanded Card #######################################################################################################################################################
    if(store.currentList !== null && store.currentList._id === idNamePair._id){
        cardElement =
        <ListItem
            id={idNamePair._id}
            key={idNamePair._id}
            sx={{borderRadius:"10px", p: 1, bgcolor: '#8000F00F', marginTop: '15px', display: 'flex', height: 500}}
            style={{transform:"translate(1%,0%)", width: '98%'}}
            button
            onClick={(event) => {
                handlelistClick(event)
            }}
        > 
        <Container>
        <Grid container>
            <Grid item md={8}>
                <Typography sx={{fontSize: 50}}>{idNamePair.name}</Typography>
            </Grid>
            <Grid item md={12}> 
                <Typography sx={{fontSize: 20}}>{"By: USERNAME"}</Typography>
            </Grid>
            <Grid item md={12}>
                <List sx={{height:"300px", width: "100%", display: "flex", flexDirection: "column", overflow: "scroll"}} >
                    {
                        store.currentList.songs.map((song, index) => (
                            <SongCard
                                id={'playlist-song-' + (index)}
                                key={'playlist-song-' + (index)}
                                index={index}
                                song={song}
                            />
                        ))
                    }
                    {modalJSX}
                    <Button
                        disabled={!store.canAddNewSong()}
                        id='add-song-button'
                        onClick={handleAddNewSong}
                        variant="contained">
                        <AddIcon />
                    </Button>
                </List>
            </Grid>
            <Grid sx={{mt:"1%"}} item md={12}>
                <Button sx={{bgcolor:"grey", color:"black"}}>undo</Button>
                <Button sx={{bgcolor:"grey", color:"black", ml:"1%"}}>redo</Button>
                <Button onClick = {(event) => {handlePublish(event, idNamePair._id)}} sx={{bgcolor:"grey", color:"black", ml:"43%"}}>publish</Button>
                <Button sx={{bgcolor:"grey", color:"black", ml:"1%"}}>duplicate</Button>
                <Button sx={{bgcolor:"grey", color:"black", ml:"1%"}} onClick={(event) => {handleDeleteList(event, idNamePair._id)}}>delete</Button>
            </Grid>
            <Grid item md={12}>
                <IconButton sx={{transform:"translate(1700%, 0%)"}} onClick={(event) => {handleMinimize(event, idNamePair._id)}} aria-label='delete'>
                    <KeyboardDoubleArrowUpIcon sx={{fontSize:'25pt'}} />
                </IconButton>
            </Grid>
            </Grid>
            </Container>
        </ListItem>
    } 

    //Editing Card #########################################################################################################################################################
    if (editActive) {
        cardElement =
            <TextField
                margin="normal"
                required
                fullWidth
                id={"list-" + idNamePair._id}
                label="Playlist Name"
                name="name"
                autoComplete="Playlist Name"
                className='list-card'
                onKeyPress={handleKeyPress}
                onChange={handleUpdateText}
                defaultValue={idNamePair.name}
                inputProps={{style: {fontSize: 48}}}
                InputLabelProps={{style: {fontSize: 24}}}
                autoFocus
            />
    }


    return (
        cardElement
    );
}

export default ListCard;
