import { useContext, useState } from 'react'
import { GlobalStoreContext } from '../store'
import Box from '@mui/material/Box';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import ListItem from '@mui/material/ListItem';
import TextField from '@mui/material/TextField';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import ThumbDownAltIcon from '@mui/icons-material/ThumbDownAlt';
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';
import KeyboardDoubleArrowUpIcon from '@mui/icons-material/KeyboardDoubleArrowUp';
import List from '@mui/material/List';
import SongCard from './SongCard.js'
import apis from '../store/store-request-api';

/*
    This is a card in our list of top 5 lists. It lets select
    a list for editing and it has controls for changing its 
    name or deleting it.

    @author McKilla Gorilla
*/
function ListCard(props) {
    const { store } = useContext(GlobalStoreContext);
    const [editActive, setEditActive] = useState(false);
    const [text, setText] = useState("");
    const { idNamePair, selected } = props;

    function handleLoadList(event, id) {
        console.log("handleLoadList for " + id);
        if (!event.target.disabled) {
            let _id = event.target.id;
            if (_id.indexOf('list-card-text-') >= 0)
                _id = ("" + _id).substring("list-card-text-".length);

            console.log("load " + event.target.id);

            // CHANGE THE CURRENT LIST
            store.setCurrentList(id);
        }
    }

    function handleToggleEdit(event) {
        event.stopPropagation();
        toggleEdit();
    }

    function toggleEdit() {
        let newActive = !editActive;
        if (newActive) {
            store.setIsListNameEditActive();
        }
        setEditActive(newActive);
    }

    async function handleDeleteList(event, id) {
        event.stopPropagation();
        let _id = event.target.id;
        _id = ("" + _id).substring("delete-list-".length);
        store.markListForDeletion(id);
    }

    function handleKeyPress(event) {
        if (event.code === "Enter") {
            let id = event.target.id.substring("list-".length);
            store.changeListName(id, text);
            toggleEdit();
        }
    }
    function handleUpdateText(event) {
        setText(event.target.value);
    }

    async function handleExpand(event, id) {
        event.stopPropagation();
        store.setCurrentList(id);
    }

    async function handleMinimize(event, id) {
        event.stopPropagation();
        store.closeCurrentList();
    }

    let selectClass = "unselected-list-card";
    if (selected) {
        selectClass = "selected-list-card";
    }
    let cardStatus = false;
    if (store.isListNameEditActive) {
        cardStatus = true;
    }
    //Normal Card #########################################################################################################################################################
    let cardElement =
        <ListItem
            id={idNamePair._id}
            key={idNamePair._id}
            sx={{borderRadius:"10px", p: 1, bgcolor: '#8000F00F', marginTop: '15px', display: 'flex', height: 200}}
            style={{transform:"translate(1%,0%)", width: '98%'}}
            button
            onClick={(event) => {
                handleLoadList(event, idNamePair._id)
            }}
        >
            <Box sx={{p: 1, flexGrow: 1}}>
            <Typography sx={{fontSize: 50}}>{idNamePair.name}</Typography>
            <Typography sx={{mb:7, fontSize: 20}}>{"By: USERNAME"}</Typography>
            <Typography display="inline" sx={{mb:1, fontSize: 14}}>{"Published: "}</Typography>
            <Typography display="inline" sx={{color: "red", fontSize: 14}}>{"DATE PUBLISHED"}</Typography>
            <Typography display="inline" sx={{ml:20, fontSize: 14}}>{"Listens: "}</Typography>
            <Typography display="inline" sx={{color: "green", fontSize: 14}}>{"LISTENS"}</Typography>
            <Box sx={{p: 1 }}>
                <IconButton sx={{mt:5, ml:80, fontSize:'40pt'}}>
                    <ThumbUpOffAltIcon  />
                    <ThumbDownOffAltIcon style={{fontSize:'40pt'}} />
                </IconButton>
            </Box>
            <IconButton sx={{mt:19}} onClick={(event) => {handleExpand(event, idNamePair._id)}} aria-label='delete'>
                    <KeyboardDoubleArrowDownIcon sx={{fontSize:'20pt'}} />
            </IconButton>
            </Box>
        </ListItem>

    //Expanded Card #######################################################################################################################################################
    if(store.currentList !== null && store.currentList._id === idNamePair._id){
        console.log(store.currentList);
        cardElement =
        <ListItem
            id={idNamePair._id}
            key={idNamePair._id}
            sx={{borderRadius:"10px", p: 1, bgcolor: '#8000F00F', marginTop: '15px', display: 'flex', height: 200}}
            style={{transform:"translate(1%,0%)", width: '98%'}}
            button
            onClick={(event) => {
                handleLoadList(event, idNamePair._id)
            }}
        >
            <Box sx={{p: 1, flexGrow: 1}}>
            <Typography sx={{fontSize: 50}}>{idNamePair.name}</Typography>
            <Typography sx={{mb:7, fontSize: 20}}>{"By: USERNAME"}</Typography>
            <Typography display="inline" sx={{mb:1, fontSize: 14}}>{"Published: "}</Typography>
            <Typography display="inline" sx={{color: "blue", fontSize: 14}}>{"DATE PUBLISHED"}</Typography>
            <Typography display="inline" sx={{ml:20, fontSize: 14}}>{"Listens: "}</Typography>
            <Typography display="inline" sx={{color: "green", fontSize: 14}}>{"LISTENS"}</Typography>

            <Box sx={{ p: 1 }}>
                <IconButton aria-label='like'>
                    <ThumbUpOffAltIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>
            <Box sx={{ p: 1 }}>
                <IconButton aria-label='dislike'>
                    <ThumbDownOffAltIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>
            <Box sx={{ p: 1 }}>
                <IconButton onClick={handleToggleEdit} aria-label='edit'>
                    <EditIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>
            <Box sx={{ p: 1 }}>
                <IconButton onClick={(event) => handleDeleteList(event, idNamePair._id)} aria-label='delete'>
                    <DeleteIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>
            <IconButton sx={{mt:19}} onClick={(event) => {handleMinimize(event, idNamePair._id)}} aria-label='delete'>
                    <KeyboardDoubleArrowUpIcon sx={{fontSize:'20pt'}} />
            </IconButton>
            <List 
            id="playlist-cards" 
            sx={{overflow: 'scroll', bgcolor: '#8000F00F'}}
        >
            {
                store.currentList.songs.map((song, index) => (
                    <SongCard
                        id={'playlist-song-' + (index)}
                        key={'playlist-song-' + (index)}
                        index={index}
                        song={song}
                    />
                ))  
            }
         </List>
         </Box>
        </ListItem>
    }
    /*<Box sx={{p: 1 }}>
                <IconButton onClick={handleToggleEdit} aria-label='edit'>
                    <EditIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>
            <Box sx={{ p: 1 }}>
                <IconButton onClick={(event) => handleDeleteList(event, idNamePair._id)} aria-label='delete'>
                    <DeleteIcon style={{fontSize:'20pt'}} />
                </IconButton>
            </Box>*/
    //Editing Card #########################################################################################################################################################
    if (editActive) {
        cardElement =
            <TextField
                margin="normal"
                required
                fullWidth
                id={"list-" + idNamePair._id}
                label="Playlist Name"
                name="name"
                autoComplete="Playlist Name"
                className='list-card'
                onKeyPress={handleKeyPress}
                onChange={handleUpdateText}
                defaultValue={idNamePair.name}
                inputProps={{style: {fontSize: 48}}}
                InputLabelProps={{style: {fontSize: 24}}}
                autoFocus
            />
    }


    return (
        cardElement
    );
}

export default ListCard;
