import * as React from 'react';
import Box from '@mui/material/Box';
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { useContext } from 'react'
import GlobalStoreContext from '../store';

export default function SortMenu() {
  const {store} = useContext(GlobalStoreContext);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleNameSort = () => {
    store.loadIdNamePairs(function(a, b){return a.name.locale(b.name)})
    setAnchorEl(null);
  }

  return (
    <Box fontSize="100px">
      <MenuIcon
        id="basic-button"
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
        fontSize="inherit"
      >
      </MenuIcon>
      <Menu
        id="sort-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >
        <MenuItem onClick={handleNameSort}>Name (A - Z)</MenuItem>
        <MenuItem onClick={handleClose}>Publish Date (Newest)</MenuItem>
        <MenuItem onClick={handleClose}>Listens (High - Low)</MenuItem>
        <MenuItem onClick={handleClose}>Likes (High - Low)</MenuItem>
        <MenuItem onClick={handleClose}>Dislikes (High - Low)</MenuItem>
      </Menu>
    </Box>
  );
}
