import React, { useState, useContext, useEffect } from 'react'
import YouTube from 'react-youtube';
import GlobalStoreContext from '../store'
import SortMenu from './SortMenu'
import ListCard from './ListCard.js'
import MUIDeleteModal from './MUIDeleteModal'
import HomeIcon from '@mui/icons-material/HomeOutlined';
import Person from '@mui/icons-material/PersonOutline';
import People from '@mui/icons-material/Groups';
import SearchBar from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import {Tabs, Tab, getNativeSelectUtilityClasses} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Fab from '@mui/material/Fab'
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Box from '@mui/material/Box'
import FastForwardIcon from '@mui/icons-material/FastForward';
import FastRewindIcon from '@mui/icons-material/FastRewind';
import PauseIcon from '@mui/icons-material/Pause';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
/*
    This React component lists all the top5 lists in the UI.

    @author McKilla Gorilla
*/
const HomeScreen = () => {

    const { store } = useContext(GlobalStoreContext);

    const [home, setHome] = useState({
        playlistName: "Select a Playlist",
        currentSong: 0,
        song: {title: null, artist: null},
    });


    if(store.playingList !== null && home.playlistName === "Select a Playlist"){
        setHome({playlistName: store.playingList.name, currentSong: home.currentSong, song: home.song});
    }

    if(store.playingList === null && home.playlistName !== "Select a Playlist"){
        setHome({playlistName: "Select a Playlist", currentSong: home.currentSong, song: home.song});
    }

    useEffect(() => {
        store.loadIdNamePairs();
    }, []);

    function handleCreateNewList() {
        store.createNewList();
    }
    let listCard = "";

    if (store) {
        listCard = 
            <List sx={{bgcolor:"#B8B8B8", width: '50%', overflow: "scroll"}}>
            {
                store.idNamePairs.map((pair) => (
                    <ListCard
                        key={pair._id}
                        idNamePair={pair}
                        selected={false}
                    />
                ))

            }
            </List>;
    }

    //COMMENTS
    let comments = null;
    if (store.playingList !== null){
        comments = 
            <List sx={{transform:"translate(0%,4%)", width: '100%', height: "550px", overflow: "scroll"}}>
            {
                store.playingList.comments.map((c) => (
                    <ListItem sx={{mt:"5px"}}>
                        <Box sx ={{borderRadius:"5px", width:"100%", bgcolor:"#FFD117"}}>
                            <Typography sx={{ml:"5px", color:"blue", textDecoration:"underline"}}>{c.postedBy}</Typography>
                            <Typography sx={{ml:"5px", fontSize:"24px", color:"black"}}>{c.comment}</Typography>

                        </Box>
                    </ListItem>
                ))

            }
            </List>
    }
    function handleAddComment(event) {
        console.log(event.target.value);
        if(event.key === "Enter"){
            store.addComment(event.target.value);
            event.target.value="";
        }
    }

    //Tab stuff
    const [value, setValue] = useState(0);

    const handleTabs=(event, value)=>{
        console.warn(value);
        setValue(value);
    }

    //Player stuff

    let p = null;

    // THIS HAS THE YOUTUBE IDS FOR THE SONGS IN OUR PLAYLIST
    let playlist = []
    if(store.playingList !== null){
        let songs = store.playingList.songs;
        for(let i = 0; i < songs.length; i++){
            playlist.push(songs[i].youTubeId);
        }
    }

    const playerOptions = {
        height: '390',
        width: '671',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 0,
        },
    };

    // THIS FUNCTION LOADS THE CURRENT SONG INTO
    // THE PLAYER AND PLAYS IT
    function loadAndPlayCurrentSong(player) {
        let song = playlist[home.currentSong];
        //handleUpdateSongBeingPlayed(song);
        player.loadVideoById(song);
        player.playVideo();
    }

    function onPlayerReady(event) {
        p = event.target;
        loadAndPlayCurrentSong(event.target);
        event.target.playVideo();
    }

    // THIS IS OUR EVENT HANDLER FOR WHEN THE YOUTUBE PLAYER'S STATE
    // CHANGES. NOTE THAT playerStatus WILL HAVE A DIFFERENT INTEGER
    // VALUE TO REPRESENT THE TYPE OF STATE CHANGE. A playerStatus
    // VALUE OF 0 MEANS THE SONG PLAYING HAS ENDED.
    function onPlayerStateChange(event) {
        let playerStatus = event.data;
        let player = event.target;
        if (playerStatus === -1) {
            // VIDEO UNSTARTED
            console.log("-1 Video unstarted");
        } else if (playerStatus === 0) {
            // THE VIDEO HAS COMPLETED PLAYING
            console.log("0 Video ended");
            setHome({playlistName: home.playlistName, currentSong: (home.currentSong+1) % playlist.length, song: home.song});
            loadAndPlayCurrentSong(player);
        } else if (playerStatus === 1) {
            // THE VIDEO IS PLAYED
            console.log("1 Video played");
        } else if (playerStatus === 2) {
            // THE VIDEO IS PAUSED
            console.log("2 Video paused");
        } else if (playerStatus === 3) {
            // THE VIDEO IS BUFFERING
            console.log("3 Video buffering");
        } else if (playerStatus === 5) {
            // THE VIDEO HAS BEEN CUED
            console.log("5 Video cued");
        }
    }

    const handlePrevSong = () => {
        setHome({playlistName: home.playlistName, currentSong: ((home.currentSong-1)+playlist.length) % playlist.length, song: home.song});
        loadAndPlayCurrentSong(p);
    }
    const handlePauseSong = () => {
        console.log("PAUSINGPAUSINGPAUSINGHDUNDFHIHIH")
        p.pauseVideo();
    }
    const handlePlaySong = () => {
        p.playVideo();
    }
    const handleNextSong = () => {
        setHome({playlistName: home.playlistName, currentSong: (home.currentSong+1) % playlist.length, song: home.song});
        loadAndPlayCurrentSong(p);
    }

    return (
        <Box sx={{height:"94%", width: "100%", bgcolor:"#B8B8B8"}}>
            <Box sx={{width:"100%", height:"8%"}}>
                <HomeIcon sx={{border:1, mt:"0.2%", ml:"1.1%",fontSize:"60px", color:"white"}}></HomeIcon>
                <People sx={{ml:"1%", mt:"0.2%",fontSize:"60px", color:"white"}}></People>
                <Person sx={{ml:"1%", mt:"0.2%",fontSize:"60px", color:"white"}}></Person>
                <SearchBar sx={{ml:"5%", mt:"0.5%", bgcolor:"white", width:"40%", color:"red"}} label="Search" variant="outlined" />
                <Box sx={{width:"100%", transform: "translate(86%, -110%)"}}>
                    <Typography sx={{fontWeight:"bold", fontSize:"30px"}}>Sort By</Typography>
                </Box>
                <Box sx={{width:"100%", transform: "translate(93%, -100%)"}}>
                    <SortMenu></SortMenu>
                </Box>
            </Box>
            <Box sx={{ml:"2%", mr:"2%",  bgcolor:"#B8B8B8"}} >
                <Box sx={{width:"190vw", height: "75vh", display: "flex", direction: "column"}}>
                    {listCard}

                </Box>
                <MUIDeleteModal />
            </Box>
            <Box sx={{transform:"translate(0%, -12%)", height:"125px", bgcolor:"#B8B8B8"}}>
                <Fab sx={{mt:"1.5%", ml:"40%", bgcolor:"transparent"}} color="primary"  aria-label="add"  id="add-list-button" onClick={handleCreateNewList}>
                    <AddIcon sx={{color:"black", fontSize:"70px"}}/>
                </Fab>
                <Typography sx={{fontSize:70, ml:"40%"}}>Your Lists</Typography>
            </Box>
        </Box>
    );
}
export default HomeScreen;

function TabPanel(props){
    const {children, value, index} = props;
    return(
    <div>
        {
            value===index  && (
                <h1>{children}</h1>
            )
        }
    </div>
    )
}
