import React, { useContext, useEffect } from 'react'
import GlobalStoreContext from '../store'
import ListCard from './ListCard.js'
import YouTubePlayerExample from '../YouTubePlaylisterReact-main/src/PlaylisterYouTubePlayer.js'
import MUIDeleteModal from './MUIDeleteModal'
import HomeIcon from '@mui/icons-material/HomeOutlined';
import Person from '@mui/icons-material/PersonOutline';
import People from '@mui/icons-material/Groups';
import SearchBar from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Tab from '@mui/material/Tab';
import AddIcon from '@mui/icons-material/Add';
import Fab from '@mui/material/Fab'
import List from '@mui/material/List';
import Box from '@mui/material/Box'
import MenuIcon from '@mui/icons-material/Menu';
import {TabContext, TabPanel, TabList} from '@mui/lab';
/*
    This React component lists all the top5 lists in the UI.

    @author McKilla Gorilla
*/
const HomeScreen = () => {
    const { store } = useContext(GlobalStoreContext);

    useEffect(() => {
        store.loadIdNamePairs();
    }, []);

    function handleCreateNewList() {
        store.createNewList();
    }
    let listCard = "";
    if (store) {
        listCard = 
            <List sx={{bgcolor:"pink", width: '50%', overflow: "scroll"}}>
            {
                store.idNamePairs.map((pair) => (
                    <ListCard
                        key={pair._id}
                        idNamePair={pair}
                        selected={false}
                    />
                ))

            }
            </List>;
    }
    return (
        <Box sx={{height:"94%", width: "100%", bgcolor:"green"}}>
            <Box sx={{width:"100%", height:"8%"}}>
                <HomeIcon sx={{border:1, mt:"0.2%", ml:"1.1%",fontSize:"60px", color:"white"}}></HomeIcon>
                <People sx={{ml:"1%", mt:"0.2%",fontSize:"60px", color:"white"}}></People>
                <Person sx={{ml:"1%", mt:"0.2%",fontSize:"60px", color:"white"}}></Person>
                <SearchBar sx={{ml:"5%", mt:"0.5%", bgcolor:"white", width:"40%", color:"red"}} label="Search" variant="outlined" />
                <Typography display="inline" sx={{ml:"30%",fontSize:"20px"}}>Sort By</Typography>
                <MenuIcon sx={{ml:"2%",fontSize:"60px", color:"black"}}></MenuIcon>
            </Box>
            <Box sx={{ml:"2%", mr:"2%",  bgcolor:"blue"}} >
                <Box sx={{height: "75vh", display: "flex", direction: "column", overflow: "scroll"}}>
                    {listCard}

                    <Box sx={{height:"6.5%", width:"48vw", bgcolor:"purple"}}>     
                            <Tab sx={{bgcolor:"grey", borderRadius:"30%"}} label="Item One" value="1" />
                            <Tab sx={{bgcolor:"grey", borderRadius:"30%"}} label="Item Two" value="2" />
                            <Box sx={{height: "1000%", width:"100%", mt:"10px", ml:"10px"}} bgcolor="white">
                                <YouTubePlayerExample></YouTubePlayerExample>
                                <SearchBar sx={{borderRadius:"20%", mt:"73%", bgcolor:"white", width:"95%", color:"red"}} label="Add Comment" variant="outlined" />
                            </Box>
                    </Box>
                </Box>
                <MUIDeleteModal />
            </Box>
            <Box sx={{bgcolor:"orange"}}>
                <Fab sx={{mt:"1.5%", ml:"40%", bgcolor:"transparent"}} color="primary"  aria-label="add"  id="add-list-button" onClick={handleCreateNewList}>
                    <AddIcon sx={{color:"black", fontSize:"70px"}}/>
                </Fab>
                <Typography sx={{fontSize:70, ml:"40%"}}>Your Lists</Typography>
            </Box>
        </Box>
    );
}
export default HomeScreen;
